var through = require( "through" );
var path = require( "path" );

module.exports = function( file, opts ) {
	var data = "";
	if( file !== undefined && path.extname( file ) !== ".robot" )
		return through();
	else
		return through( write, end );

	function write(buf) {
		data += buf;
	}

	function end() {
		this.queue( "var _ = require( 'underscore' );" );
		this.queue( null );
	}
};